mapper.insert:
INSERT INTO spoon_board ( board_subject, board_contents, board_hits, subway_name, mid, board_address, board_lat, board_lng, board_shopname
) VALUES ( #{board_subject}, #{board_contents}, 0, #{subway_name}, #{mid}, #{board_address}, #{board_lat}, #{board_lng}, #{board_shopname}
)

mapper.select:
SELECT subway_name,subway_linenumber FROM SPOON_SUBWAY 
WHERE SUBWAY_LINENUMBER = #{subway_linenumber}
ORDER BY SUBWAY_NAME ASC
