mapper.insert:
INSERT INTO spoon_board ( board_subject, board_contents, board_hits, subway_name, mid, member_id, board_address, board_lat, board_lng, board_shopname
) VALUES ( #{board_subject}, #{board_contents}, 0, #{subway_name}, #{mid}, #{member_id}, #{board_address}, #{board_lat}, #{board_lng}, #{board_shopname}
)

mapper.select:
SELECT subway_name,subway_linenumber FROM SPOON_SUBWAY 
WHERE SUBWAY_LINENUMBER = #{subway_linenumber}
ORDER BY SUBWAY_NAME ASC

mapper.update:
UPDATE SPOON_BOARD SET BOARD_LIKE=(SELECT ifnull (SUM(BOARD_LIKE), 0 ) FROM BOARD_LIKE_HATE WHERE BID = #{bid}),
				BOARD_HATE = (SELECT ifnull (SUM(BOARD_HATE), 0 ) FROM BOARD_LIKE_HATE WHERE BID = #{bid})
			WHERE BID = #{bid}
